import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";

export default function ForgotPasswordMethod() {
  const [location] = useLocation();
  const [email, setEmail] = useState("");
  const [recoveryEmail, setRecoveryEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [submitted, setSubmitted] = useState(false);
  const [recoveryCode, setRecoveryCode] = useState("");
  const [documentNumber, setDocumentNumber] = useState("");
  const { toast } = useToast();

  const isSms = location.includes("/sms");

  useEffect(() => {
    // Extract documentNumber from URL search params
    const fullUrl = window.location.href;
    const url = new URL(fullUrl);
    const docNum = url.searchParams.get("documentNumber") || "";
    setDocumentNumber(docNum);
  }, []);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (isSms && !phone) {
      toast({
        title: "Error",
        description: "Por favor ingresa tu tel√©fono",
        variant: "destructive",
      });
      return;
    }

    if (!isSms && !recoveryEmail) {
      toast({
        title: "Error",
        description: "Por favor ingresa tu email de recuperaci√≥n",
        variant: "destructive",
      });
      return;
    }

    setIsLoading(true);

    try {
      const endpoint = isSms
        ? "/api/password-reset/request-sms"
        : "/api/password-reset/request-email";
      const payload = isSms 
        ? { phone, documentNumber } 
        : { email: recoveryEmail, documentNumber };

      const res = await fetch(endpoint, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });

      if (!res.ok) {
        const error = await res.json();
        toast({
          title: "Error",
          description: error.error || "No se pudo enviar el c√≥digo",
          variant: "destructive",
        });
        setIsLoading(false);
        return;
      }

      const data = await res.json();

      toast({
        title: "√âxito",
        description: isSms ? "C√≥digo enviado por SMS" : "C√≥digo enviado por email",
      });

      if (data.code) {
        setRecoveryCode(data.code);
      }

      setSubmitted(true);
    } catch (error) {
      toast({
        title: "Error",
        description: "Error al procesar la solicitud",
        variant: "destructive",
      });
      setIsLoading(false);
    }
  };

  if (submitted) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-muted/30 p-4">
        <Card className="w-full max-w-md">
          <CardHeader>
            <CardTitle>C√≥digo Enviado</CardTitle>
            <CardDescription>
              {isSms ? `Verifica tu tel√©fono ${phone}` : `Verifica tu email ${email}`}
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <p className="text-sm text-muted-foreground">
              Hemos enviado un c√≥digo de 6 d√≠gitos. Ser√° v√°lido por 15 minutos.
            </p>

            {recoveryCode && (
              <div className="bg-muted p-4 rounded-md border border-border">
                <p className="text-sm text-muted-foreground mb-2">Tu c√≥digo es:</p>
                <p className="text-2xl font-bold text-center font-mono" data-testid="text-recovery-code">
                  {recoveryCode}
                </p>
              </div>
            )}

            <a href={`/admin-secret-2024/reset-password?documentNumber=${encodeURIComponent(documentNumber)}`}>
              <Button className="w-full" data-testid="button-go-to-reset">
                Continuar a Resetear Contrase√±a
              </Button>
            </a>

            <Button
              variant="outline"
              className="w-full"
              onClick={() => {
                setSubmitted(false);
                setRecoveryCode("");
                setPhone("");
              }}
              data-testid="button-change-method"
            >
              Cambiar forma de recuperaci√≥n
            </Button>

            <a href="/admin-secret-2024" className="block">
              <Button variant="ghost" className="w-full" type="button" data-testid="button-back-to-login">
                Volver al Login
              </Button>
            </a>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-muted/30 p-4">
      <Card className="w-full max-w-md">
        <CardHeader>
          <CardTitle>Recuperar Contrase√±a</CardTitle>
          <CardDescription>
            {isSms ? "Ingresa tu n√∫mero de tel√©fono" : "Un c√≥digo ser√° enviado a tu email"}
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          <form onSubmit={handleSubmit} className="space-y-4">
            {isSms ? (
              <div>
                <Label htmlFor="phone">Tel√©fono</Label>
                <input
                  id="phone"
                  type="tel"
                  value={phone}
                  onChange={(e) => setPhone(e.target.value)}
                  placeholder="+51 999 888 777"
                  disabled={isLoading}
                  data-testid="input-phone"
                  required
                  className="flex h-9 w-full rounded-md border border-input bg-background px-3 py-2 text-base ring-offset-background placeholder:text-muted-foreground focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:cursor-not-allowed disabled:opacity-50 md:text-sm"
                />
              </div>
            ) : (
              <div>
                <Label htmlFor="recoveryEmail">Email para recibir el c√≥digo</Label>
                <input
                  id="recoveryEmail"
                  type="text"
                  value={recoveryEmail}
                  onChange={(e) => setRecoveryEmail(e.target.value)}
                  placeholder="tu.email@ejemplo.com"
                  data-testid="input-recovery-email"
                  className="flex h-9 w-full rounded-md border border-input bg-background px-3 py-2 text-base ring-offset-background placeholder:text-muted-foreground focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:cursor-not-allowed disabled:opacity-50 md:text-sm"
                  autoFocus
                />
                <p className="text-xs text-muted-foreground mt-1">
                  Ingresa tu email de recuperaci√≥n registrado
