      toast({
        title: "Error",
        description: "Las contrase√±as no coinciden",
        variant: "destructive",
      });
      return;
    }

    if (documentType === "DNI" && documentNumber.length !== 8) {
      toast({
        title: "Error",
        description: "El DNI debe tener exactamente 8 caracteres",
        variant: "destructive",
      });
      return;
    }

