client/src/pages/AdminDashboard.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { LogOut } from "lucide-react";
import { Card, CardContent, CardFooter, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Switch } from "@/components/ui/switch";
import { Label } from "@/components/ui/label";
import { Plus, Edit2, Trash2, X, Facebook, Instagram, MessageCircle, MapPin, Phone, Mail } from "lucide-react";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import type { Product, ITService, FoodItem, Footer, AdminUser } from "@shared/schema";

const productCategories = [
  "camisetas", "pantalones", "mochilas", "zapatos", "vestidos", "accesorios",
  "laptops", "pcs", "monitores", "perifericos", "componentes", "smartphones",
];

const foodCategories = [
  "desayunos", "almuerzos", "snacks", "bebidas", "postres",
];

// ===== PRODUCTOS TECH =====
function ProductsTab() {
  const [isAddOpen, setIsAddOpen] = useState(false);
  const [editingProduct, setEditingProduct] = useState<Product | null>(null);
  const [newCategory, setNewCategory] = useState("");
  const [showNewCategory, setShowNewCategory] = useState(false);
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    price: "",
    category: "",
    image: "",
    inStock: true,
  });
  const { toast } = useToast();

  const { data: products = [], isLoading } = useQuery<Product[]>({
    queryKey: ["/api/products"],
  });

  const { data: categoriesData = [] } = useQuery({
    queryKey: ["/api/product-categories"],
  });

  const categories = categoriesData.map((cat: any) => cat.name);

  const createCategoryMutation = useMutation({
    mutationFn: async (name: string) => {
      return apiRequest("POST", "/api/product-categories", { name });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/product-categories"] });
      setNewCategory("");
      setShowNewCategory(false);
      toast({ title: "Categor√≠a agregada exitosamente" });
    },
    onError: () => {
      toast({ title: "Error", description: "No se pudo crear la categor√≠a", variant: "destructive" });
    },
  });

  const deleteCategoryMutation = useMutation({
    mutationFn: async (categoryId: string) => {
      return apiRequest("DELETE", `/api/product-categories/${categoryId}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/product-categories"] });
      toast({ title: "Categor√≠a eliminada" });
    },
  });

  const createMutation = useMutation({
    mutationFn: async (data: typeof formData) => {
      return apiRequest("POST", "/api/products", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/products"] });
      resetForm();
      setIsAddOpen(false);
      toast({ title: "Producto creado exitosamente" });
    },
    onError: () => {
      toast({ title: "Error", description: "No se pudo crear el producto", variant: "destructive" });
    },
  });

  const updateMutation = useMutation({
    mutationFn: async (data: typeof formData) => {
      if (!editingProduct) throw new Error("No product selected");
      return apiRequest("PUT", `/api/products/${editingProduct.id}`, data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/products"] });
      resetForm();
      setEditingProduct(null);
      setIsAddOpen(false);
      toast({ title: "Producto actualizado exitosamente" });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      return apiRequest("DELETE", `/api/products/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/products"] });
      toast({ title: "Producto eliminado" });
    },
  });

  const resetForm = () => {
    setFormData({
      name: "",
      description: "",
      price: "",
      category: "",
      image: "",
      inStock: true,
    });
  };

  const handleEdit = (product: Product) => {
    setEditingProduct(product);
    setFormData({
      name: product.name,
      description: product.description,
      price: product.price,
      category: product.category,
      image: product.image,
      inStock: product.inStock,
    });
    setIsAddOpen(true);
  };

  const handleSubmit = () => {
    if (!formData.name || !formData.category || !formData.price) {
      toast({ title: "Completa los campos obligatorios", variant: "destructive" });
      return;
    }

    if (editingProduct) {
      updateMutation.mutate(formData);
    } else {
      createMutation.mutate(formData);
    }
  };

  const addCategory = () => {
    if (newCategory.trim() && !categories.includes(newCategory)) {
      createCategoryMutation.mutate(newCategory);
    }
  };

  const deleteCategory = (catId: string) => {
    deleteCategoryMutation.mutate(catId);
  };

  if (isLoading) return <div className="p-8">Cargando...</div>;

  return (
    <div className="space-y-6">
      {/* Gesti√≥n de Categor√≠as */}
      <Card>
        <CardHeader>
          <CardTitle>Gestionar Categor√≠as</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="flex flex-wrap gap-2 mb-4">
            {categoriesData.map((cat: any) => (
              <Badge key={cat.id} variant="secondary" className="flex items-center gap-2">
                {cat.name}
                <button onClick={() => deleteCategory(cat.id)} className="ml-1 hover:text-red-600">√ó</button>
              </Badge>
            ))}
          </div>
          {showNewCategory ? (
            <div className="flex gap-2">
              <Input
                placeholder="Nueva categor√≠a"
                value={newCategory}
                onChange={(e) => setNewCategory(e.target.value)}
              />
              <Button onClick={addCategory} size="sm">Agregar</Button>
              <Button onClick={() => setShowNewCategory(false)} size="sm" variant="outline">Cancelar</Button>
            </div>
          ) : (
            <Button onClick={() => setShowNewCategory(true)} size="sm">
              <Plus className="h-4 w-4 mr-2" /> Nueva Categor√≠a
            </Button>
          )}
        </CardContent>
      </Card>

      {/* Productos */}
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold">Productos</h2>
        <Dialog open={isAddOpen} onOpenChange={setIsAddOpen}>
          <DialogTrigger asChild>
            <Button onClick={() => { resetForm(); setEditingProduct(null); }}>
              <Plus className="h-4 w-4 mr-2" /> Agregar Producto
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-md">
            <DialogHeader>
              <DialogTitle>{editingProduct ? "Editar" : "Nuevo"} Producto</DialogTitle>
            </DialogHeader>
            <div className="space-y-4">
              <Input
                placeholder="Nombre"
                value={formData.name}
                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
              />
              <Textarea
                placeholder="Descripci√≥n"
                value={formData.description}
                onChange={(e) => setFormData({ ...formData, description: e.target.value })}
              />
              <Input
                placeholder="Precio"
                type="number"
                step="0.01"
                value={formData.price}
                onChange={(e) => setFormData({ ...formData, price: e.target.value })}
              />
              <Select value={formData.category} onValueChange={(val) => setFormData({ ...formData, category: val })}>
                <SelectTrigger>
                  <SelectValue placeholder="Categor√≠a" />
                </SelectTrigger>
                <SelectContent>
                  {categories.map((cat) => (
                    <SelectItem key={cat} value={cat}>{cat}</SelectItem>
                  ))}
                </SelectContent>
              </Select>
              <Input
                placeholder="URL de imagen"
                value={formData.image}
                onChange={(e) => setFormData({ ...formData, image: e.target.value })}
              />
              <div className="flex items-center justify-between">
                <Label>En Stock</Label>
                <Switch
                  checked={formData.inStock}
                  onCheckedChange={(val) => setFormData({ ...formData, inStock: val })}
                />
              </div>
              <Button
                onClick={handleSubmit}
                disabled={createMutation.isPending || updateMutation.isPending}
                className="w-full"
              >
                {editingProduct ? "Actualizar" : "Crear"}
              </Button>
            </div>
          </DialogContent>
        </Dialog>
      </div>

      {editingProduct && (
        <div className="p-4 bg-blue-50 rounded-lg flex justify-between items-center">
          <span>Editando: <strong>{editingProduct.name}</strong></span>
          <Button variant="ghost" size="sm" onClick={() => { setEditingProduct(null); resetForm(); }}>
            <X className="h-4 w-4" />
          </Button>
        </div>
      )}

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {products.map((product) => (
          <Card key={product.id} className="flex flex-col">
            <CardHeader>
              <CardTitle className="text-lg">{product.name}</CardTitle>
              <p className="text-sm text-muted-foreground">{product.category}</p>
            </CardHeader>
            <CardContent className="flex-1">
              <p className="text-sm text-muted-foreground mb-2">{product.description}</p>
              <p className="text-2xl font-bold text-primary mb-2">S/ {product.price}</p>
              <Badge variant={product.inStock ? "default" : "destructive"}>
                {product.inStock ? "En Stock" : "Agotado"}
              </Badge>
            </CardContent>
            <CardFooter className="flex gap-2">
              <Button size="sm" variant="outline" onClick={() => handleEdit(product)} className="flex-1">
                <Edit2 className="h-4 w-4 mr-1" /> Editar
              </Button>
              <Button size="sm" variant="destructive" onClick={() => deleteMutation.mutate(product.id)} className="flex-1">
                <Trash2 className="h-4 w-4 mr-1" /> Eliminar
              </Button>
            </CardFooter>
          </Card>
        ))}
      </div>
    </div>
  );
}

// ===== SERVICIOS IT =====
function ITServicesTab() {
  const [isAddOpen, setIsAddOpen] = useState(false);
  const [editingService, setEditingService] = useState<ITService | null>(null);
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    features: "",
    icon: "Wrench",
    available: true,
  });
  const { toast } = useToast();

  const { data: services = [], isLoading } = useQuery<ITService[]>({
    queryKey: ["/api/it-services"],
  });

  const createMutation = useMutation({
    mutationFn: async (data: typeof formData) => {
      return apiRequest("POST", "/api/it-services", {
        title: data.title,
        description: data.description,
        features: data.features.split("\n").filter(f => f.trim()),
        icon: data.icon,
        available: data.available,
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/it-services"] });
      resetForm();
      setIsAddOpen(false);
      toast({ title: "Servicio creado exitosamente" });
    },
  });

  const updateMutation = useMutation({
    mutationFn: async (data: typeof formData) => {
      if (!editingService) throw new Error("No service selected");
