// ===== ADMIN MANAGEMENT =====
function AdminsTab() {
  const [isAddOpen, setIsAddOpen] = useState(false);
  const [editingAdmin, setEditingAdmin] = useState<AdminUser | null>(null);
  const [formData, setFormData] = useState({ email: "", password: "", fullName: "", role: "editor", documentType: "DNI", documentNumber: "", recoveryEmail: "" });
  const { toast } = useToast();

  const { data: admins = [], isLoading } = useQuery({
    queryKey: ["/api/admins"],
  });

  const createMutation = useMutation({
    mutationFn: async (data: { email: string; password: string; fullName?: string; role: string; documentType: string; documentNumber: string; recoveryEmail: string }) => {
      return apiRequest("POST", "/api/admins", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admins"] });
      setFormData({ email: "", password: "", fullName: "", role: "editor", documentType: "DNI", documentNumber: "", recoveryEmail: "" });
      setIsAddOpen(false);
      toast({ title: "Admin creado exitosamente" });
    },
    onError: () => {
      toast({ title: "Error", description: "No se pudo crear el admin", variant: "destructive" });
    },
  });

  const updateMutation = useMutation({
    mutationFn: async (data: { role: string; fullName?: string }) => {
      if (!editingAdmin) throw new Error("No admin selected");
      return apiRequest("PUT", `/api/admins/${editingAdmin.id}`, data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admins"] });
      setFormData({ email: "", password: "", fullName: "", role: "editor", documentType: "DNI", documentNumber: "", recoveryEmail: "" });
      setEditingAdmin(null);
      toast({ title: "Admin actualizado exitosamente" });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      return apiRequest("DELETE", `/api/admins/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admins"] });
      toast({ title: "Admin eliminado" });
    },
  });

  const handleSubmit = () => {
    if (!formData.email) {
      toast({ title: "El email es requerido", variant: "destructive" });
      return;
    }

    // For creating new admin, password is required
    if (!editingAdmin && !formData.password) {
      toast({ title: "La contrase√±a es requerida para nuevo admin", variant: "destructive" });
      return;
    }

    // For creating new admin, document info and recovery email are required
    if (!editingAdmin) {
      if (!formData.documentNumber || !formData.recoveryEmail) {
        toast({ title: "Por favor completa n√∫mero de documento y email de recuperaci√≥n", variant: "destructive" });
        return;
      }
      if (formData.documentType === "DNI" && formData.documentNumber.length !== 8) {
        toast({ title: "El DNI debe tener exactamente 8 caracteres", variant: "destructive" });
        return;
      }
    }

    if (editingAdmin) {
      // When editing, password is optional (can be empty string to skip changing it)
      updateMutation.mutate({ 
        role: formData.role, 
        fullName: formData.fullName || undefined,
        password: formData.password || undefined // Pass undefined if empty to skip password change
      });
    } else {
      createMutation.mutate(formData);
    }
  };

  return (
    <div className="space-y-6">
      <div className="flex gap-2">
